#!/bin/bash
set -e

# 6.44. Autoconf-2.69
# The Autoconf package contains programs for producing shell scripts that can
# automatically configure source code.

echo "Building Autoconf..."
echo "Approximate build time: less than 0.1 SBU (about 3.2 SBU with tests)"
echo "Required disk space: 79 MB"

tar -xf /sources/autoconf-*.tar.* -C /tmp/ \
  && mv /tmp/autoconf-* /tmp/autoconf \
  && pushd /tmp/autoconf

# First, fix a bug generated by Perl 5.28.:
sed '361 s/{/\\{/' -i bin/autoscan.in

# Prepare Autoconf for compilation:
./configure --prefix=/usr

# Compile the package:
make

# The test suite is currently broken by bash-5 and libtool-2.4.3. Run the tests anyway:
if [ $LFS_TEST -eq 1 ]; then make check || true; fi

# Install the package:
make install

popd \
  && rm -rf /tmp/autoconf
